<div class="com_shop">    <form target="_self" name="adminForm" action="<?php echo admin_url('admin.php?page=component_com_shop-country'); ?>" method="post">         <input type='hidden' name='task' value='save_country' />        <input type="hidden" value="<?php echo (int) $this->row->country_id; ?>" name="country_id">        <fieldset class="panelform">            <ul class="adminformlist">                <li>                    <label for="country_name">                        <?php _e('Country Name:', 'com_shop'); ?>                    </label>                    <input type="text" value="<?php echo strings::htmlentities($this->row->country_name); ?>" name="country_name" id="country_name" />                </li>                <li>                    <label for="country_2_code">                        <?php _e('Country Code (2):', 'com_shop'); ?>                    </label>                    <input type="text" value="<?php echo strings::htmlentities($this->row->country_2_code); ?>" name="country_2_code" id="country_2_code" />                </li>                <li>                    <label for="country_3_code">                        <?php _e('Country Code (3):', 'com_shop'); ?>                    </label>                    <input type="text" value="<?php echo strings::htmlentities($this->row->country_3_code); ?>" name="country_3_code" id="country_3_code">                </li>                <li>                    <label>                        <?php _e('In EU:', 'com_shop'); ?>                    </label>                    <fieldset class="panelform">                        <input type="radio" value="no" name="in_eu" id="in_eu_no" <?php echo $this->row->in_eu == 'no' ? "checked='checked'" : ''; ?> />                        <label for="in_eu_no"><?php _e('No', 'com_shop'); ?></label>                        <input type="radio" value="yes" id="in_eu_yes" name="in_eu" <?php echo $this->row->in_eu == 'yes' ? "checked='checked'" : ''; ?> />                        <label for="in_eu_yes"><?php _e('Yes', 'com_shop'); ?></label>                    </fieldset>                </li>            </ul>        </fieldset>    </form></div>