<div class="com_shop">    <form name='adminForm' action='<?php echo admin_url('admin.php?page=component_com_shop-payment'); ?>' method='post'>        <fieldset class="panelform">            <ul class="adminformlist">                <li>                    <label for="name">                        <?php _e('Display name:', 'com_shop'); ?>                    </label>                    <input name="name" id="name" value="<?php echo strings::htmlentities($this->row->name); ?>" type="text">                           </li>                <li>                    <label for="method_order"><?php _e('Listorder:', 'com_shop'); ?></label>                    <input name="method_order" id="method_order" value="<?php echo $this->row->method_order; ?>" type="text">                </li>                <li>                    <label for="class"><?php _e('Predefined class:', 'com_shop'); ?></label>                    <select name="class" id="class" onchange="jsShopAdminHelper.payment.loadClassOptions(this.value);">                        <option value=""></option>                        <?php foreach ((array) $this->class_names as $class) { ?>                            <option value="<?php echo $class; ?>" <?php echo ($this->row->class == $class ) ? "selected='selected'" : ""; ?> ><?php echo $class; ?></option>                            <?php } ?>                        </select>                    </li>                    <li>                        <label for="countries"><?php _e('Available in:', 'com_shop'); ?></label>                        <select multiple="multiple" size="5" name="countries[]" id="countries">                            <option value=""><?php _e('--All countries--', 'com_shop'); ?></option>                            <?php foreach ((array) $this->countries as $o) { ?>                             <option value="<?php echo $o->country_2_code; ?>" <?php echo (in_array($o->country_2_code, $this->row->countries))  ? "selected='selected'"     : ""; ?> ><?php echo strings::htmlentities($o->country_name); ?></option>                            <?php } ?>                        </select>                    </li>                </ul>            </fieldset>            <h3><?php _e("Method options", "com_shop"); ?></h3>            <div id="class_options" class="class_options">                <?php echo $this->class_options; ?>            </div>            <input type='hidden' name='task' value='save' />            <input name="method_id" value="<?php echo $this->row->method_id; ?>   " type="hidden">    </form></div>