<div class="com_shop">    <form name='adminForm' action='<?php echo admin_url('admin.php?page=component_com_shop-shipping'); ?>' method='post'>        <fieldset class="panelform">            <ul class="adminformlist">                <li>                    <label for="name">                        <?php _e('Shipper Company:', 'com_shop'); ?>                    </label>                    <input id="name" name="name" value="<?php echo strings::htmlentities($this->row->name); ?>" type="text" />                </li>                <li>                    <label for="method_order">                        <?php _e('Listorder:', 'com_shop'); ?>                    </label>                    <input name="method_order" id="method_order" value="<?php echo $this->row->method_order; ?>" type="text" />                </li>                <li>                    <label for="class">                        <?php _e('Predefined class:', 'com_shop'); ?>                    </label>                    <select name="class" id="class" onchange="jsShopAdminHelper.shipping.loadClassOptions(this.value,<?php echo (string) (int) $this->row->method_id; ?>);">                        <?php foreach ((array) $this->class_names as $class) { ?>                            <option value="<?php echo $class; ?>" <?php echo ($this->row->class == $class) ? "selected='selected'" : ""; ?> ><?php echo strings::htmlentities($class); ?></option>                        <?php } ?>                    </select>                </li>            </ul>        </fieldset>        <h3><?php _e("Class options"); ?></h3>        <div id="class_options" class="class_options">            <?php echo $this->class_options; ?>        </div>        <input type='hidden' name='task' value='save' />        <input name="method_id" value="<?php echo $this->row->method_id; ?>" type="hidden">    </form></div>