<div class="com_shop">    <form name='adminForm' action='<?php echo admin_url('admin.php?page=component_com_shop-tax'); ?>' method='post'>        <input type='hidden' name='task' value='delete_rate' />        <table class="wp-list-table widefat fixed tax-rates">            <thead>                <tr>                    <th width="2%">#</th>                    <th style="padding:0;" width="3%" ><input type="checkbox" id='toggle' value="" name="toggle"></th>                    <th></th>                    <th width="" class="title"><?php _e('Country Name', 'com_shop'); ?></th>                    <th width="" class="title"><?php _e('State Name', 'com_shop'); ?></th>                    <th width="" class="title"><?php _e('Tax Name', 'com_shop'); ?></th>                    <th width="" class="title"><?php _e('Tax Priority', 'com_shop'); ?></th>                    <th width="" class="title"><?php _e('Rate', 'com_shop'); ?></th>                </tr>            </thead>            <tbody>                <?php                $c = 0;                while ($o = $this->res->nextObject()) {                    $c++;                    ?>                    <tr class="row<?php echo (int) (bool) $c % 2; ?>">                        <td><?php echo $c; ?></td>                        <td><input type="checkbox"  value="<?php echo $o->tax_rate_id; ?>" name="tax_rate_ids[]" ></td>                        <td>                            <button onclick="window.location.href = '<?php echo admin_url('admin.php?page=component_com_shop-tax&task=add_rate&tax_rate_id=' . $o->tax_rate_id); ?>';                                    return false;" class="btn btn-small">                                <span class="icon-edit">                                </span>                                <?php _e('Edit', 'com_shop'); ?>                            </button>                        </td>                        <td><?php echo empty($o->country_name) ? "-" : strings::htmlentities($o->country_name); ?></td>                        <td><?php echo empty($o->state_name) ? "-" : strings::htmlentities($o->state_name); ?></td>                        <td><?php echo empty($o->tax_name) ? "-" : strings::htmlentities($o->tax_name); ?></td>                        <td><?php echo empty($o->priority) ? "1" : strings::htmlentities($o->priority); ?></td>                        <td><?php echo $o->tax_rate; ?> %</td>                    </tr>                <?php } ?>            </tbody>        </table>    </form></div>